import React, { Component } from "react";
import { Route, withRouter } from "react-router-dom";
import { Link } from "react-router-dom";
import PropTypes from "prop-types";
import ActiveUser from "./ActiveUser";
import AdditionalUsers from "./AdditionalUsers";
import NoAdditionalUsers from "./NoAdditionalUsers";

export class UserOverview extends Component {
  onClick = () => {
    // Redirect to settings route
    this.props.history.push("/home");
  };

  render() {
    return (
      <div>
        <div className='settings-content'>
          <div className='settings-header'>
            <span>
              <button type='button' onClick={this.onClick}>
                &times;
              </button>
              {/* <Link style={removeTextDecoration} className="close-settings" to="/"> &times; </Link> */}
            </span>
            <h2>Settings</h2>
          </div>
          <div className='settings-body'>
            <ActiveUser activeUser={this.props.activeUser} />
            {this.props.users.length > 1 ? (
              <AdditionalUsers users={this.props.users} />
            ) : (
              <NoAdditionalUsers />
            )}

            <div id='settings-buttons'>
              <Link
                style={removeTextDecoration}
                className='settings-button'
                id='new-user-button'
                to='/settings/adduser'
              >
                Add User
              </Link>
              <button
                type='button'
                className='settings-button'
                id='reset-users-button'
              >
                Reset Users
              </button>
            </div>
          </div>
          <div className='settings-footer'></div>
        </div>
      </div>
    );
  }
}

const removeTextDecoration = {
  textDecoration: "none"
};

// Property Type Requirement
UserOverview.propTypes = {
  users: PropTypes.array.isRequired,
  activeUser: PropTypes.func.isRequired
};

export default withRouter(UserOverview);
