        firstName: "Tabby",
        lastName: "Hussain",
        dobDay: 13,
        dobMonth: 11,
        country: "China",
        active: false
      }
    ]
  }

  getActiveUser = () => {
    return this.state.users.filter(
      user => {
          return user.active    
      }
  )[0]
  }



  getCurrentHour = () => {
    return new Date().getHours();
  }

  getCurrentDay = () => {
    return new Date().getDate();
  }

  getCurrentMonth = () => {
    return new Date().getMonth();
  }


