import React, { Component } from "react";
import { BrowserRouter, Route, Switch, Redirect } from "react-router-dom";
import "./App.css";
import Header from "./components/layout/Header";
import Settings from "./components/settings/Settings";
import Home from "./components/home/Home";
import Todos from "./components/todos/Todos";

export class App extends Component {
  state = {
    users: [
      {
        id: "0",
        firstName: "",
        lastName: "",
        dobDay: 0,
        dobMonth: 0,
        country: "USA",
        todos: [],
        active: false
      },
      {
        id: "1",
        firstName: "Wajeeh",
        lastName: "Anwar",
        dobDay: 2,
        dobMonth: 3,
        country: "USA",
        todos: [
          {
            id: "123",
            title: "testing todo 1",
            completed: false
          },
          {
            id: "124",
            title: "testing todo 2",
            completed: false
          },
          {
            id: "125",
            title: "testing todo 3",
            completed: true
          },
          {
            id: "126",
            title: "testing todo 4",
            completed: false
          }
        ],
        active: true
      },
      {
        id: "2",
        firstName: "Tabby",
        lastName: "Hussain",
        dobDay: 13,
        dobMonth: 11,
        country: "China",
        todos: [],
        active: false
      },
      {
        id: "3",
        firstName: "Zara",
        lastName: "Anwar",
        dobDay: 25,
        dobMonth: 3,
        country: "USA",
        todos: [],
        active: false
      }
    ]
  };

  //
  // Settings functions
  //

  getActiveUser = () => {
    return this.state.users.filter(user => {
      return user.active;
    })[0];
  };

  deactivateActiveUser = () => {
    this.setState({
      users: this.state.users.map(user => {
        if (user.active) {
          user.active = false;
        }
        return user;
      })
    });
  };

  activateUser = id => {
    console.log("activate user method");
    this.deactivateActiveUser();

    // Activate this user
    this.setState({
      users: this.state.users.map(user => {
        if (user.id === id) {
          user.active = !user.active;
        }
        return user;
      })
    });
  };

  deleteUser = id => {
    console.log("delete user method");
    this.setState({
      users: this.state.users.filter(user => {
        return user.id !== id;
      })
    });
  };

  addNewUser = (firstName, lastName, dobDay, dobMonth, country) => {
    // Create new user object.
    const uniqueId = `${firstName}_${dobMonth}_${lastName}_${dobDay}_${new Date().getTime()}`;
    const newUser = {
      id: uniqueId,
      firstName: firstName,
      lastName: lastName,
      dobDay: dobDay,
      dobMonth: dobMonth,
      country: country,
      todos: [],
      active: true
    };

    this.deactivateActiveUser();
    // Copy old state.users array and add new user to end.
    this.setState({ users: [...this.state.users, newUser] });

    console.log(this.state.users);
  };

  //
  // Todo Functions
  //

  addTodo = title => {
    const uniqueId = `${title}_${new Date().getTime()}`;
    const newTodo = {
      id: uniqueId,
      title: title,
      completed: false
    };

    this.setState({
      users: this.state.users.map(user => {
        if (user.id === this.getActiveUser().id) {
          user.todos = [...user.todos, newTodo];
        }
        return user;
      })
    });
  };

  removeTodo = id => {
    console.log(id);

    let tempTodos = this.getActiveUser().todos.filter(todo => {
      return todo.id !== id;
    });

    console.log(tempTodos);

    this.setState({
      users: this.state.users.map(user => {
        if (user.id === this.getActiveUser().id) {
          return user.todos.filter(todo => {
            return todo.id !== id;
          });
        }
      })
    });
  };
  //
  // Home Functions
  //

  getCurrentHour = () => {
    return new Date().getHours();
  };

  getCurrentDay = () => {
    return new Date().getDate();
  };

  getCurrentMonth = () => {
    return new Date().getMonth() + 1;
  };

  render() {
    return (
      <div className='App'>
        <BrowserRouter>
          <div className='container'>
            <Header />
            <Switch>
              <Route exact path='/'>
                <div>
                  <Home
                    getActiveUser={this.getActiveUser}
                    getCurrentHour={this.getCurrentHour}
                    getCurrentDay={this.getCurrentDay}
                    getCurrentMonth={this.getCurrentMonth}
                  />
                </div>
              </Route>
              {/* Router */}
              <Route path='/settings'>
                <div>
                  <Settings
                    users={this.state.users}
                    activeUser={this.getActiveUser}
                    addNewUser={this.addNewUser}
                    activateUser={this.activateUser}
                    deleteUser={this.deleteUser}
                  />
                </div>
              </Route>
              {/* Router */}
              <Route>
                <div>
                  <Todos
                    activeUser={this.getActiveUser}
                    addTodo={this.addTodo}
                    removeTodo={this.removeTodo}
                  />
                </div>
              </Route>
              <Redirect exact from='/*' to='/' />
            </Switch>
          </div>
        </BrowserRouter>
      </div>
    );
  }
}

export default App;
