import React, { Component } from 'react'
import './App.css';
import Header from './components/layout/Header';
import Search from './components/Search';
import Welcome from './components/Welcome';
import Message from './components/Message';
import Settings from './components/Settings';


export class App extends Component {

  state = {
    users: [
      {
        id: 0,
        firstName: "",
        lastName: "",
        dobDay: 0,
        dobMonth: 0,
        country: "USA",
        active: false
      },
      {
        id: 1,
        firstName: "Wajeeh",
        lastName: "Anwar",
        dobDay: 2,
        dobMonth: 3,
        country: "USA",
        active: true
      }, 
      {
        id: 2,
        firstName: "Tabby",
        lastName: "Hussain",
        dobDay: 13,
        dobMonth: 11,
        country: "China",
        active: false
      },
      {
        id: 3,
        firstName: "Zara",
        lastName: "Anwar",
        dobDay: 25,
        dobMonth: 3,
        country: "USA",
        active: false
      }
    ]
  }

  getActiveUser = () => {
    return this.state.users.filter(user => {
      return user.active
    })[0]
  }

  addNewUser = (firstName, lastName, dobDay, dobMonth, country) => {
    const newId = this.state.users.length+1; 
    const newUser = {
        id: newId,
        firstName: firstName, 
        lastName: lastName, 
        dobDay: dobDay, 
        dobMonth: dobMonth, 
        country: country,
        active: true
    }

    // Find previous active user in state.users and set as inactive.
    this.setState({users: this.state.users.map(user => {
      if (user.active) {
        user.active = false
         }
        return user
        })});

    // Copy old state.users array and add new user to end.
    this.setState({users: [...this.state.users, newUser]})
  }

  getCurrentHour = () => {
    return new Date().getHours();
  }

  getCurrentDay = () => {
    return new Date().getDate();
  }

  getCurrentMonth = () => {
    return new Date().getMonth();
  }


  render() {
    return (
      <div className="App">
        <div className="container">
          <Header/>
          {/* Router */}
          <div>
            <Welcome 
              user={this.getActiveUser} 
              currentHour={this.getCurrentHour()}
            />
            <Search/>
            <Message 
              user={this.getActiveUser} 
              currentDay={this.getCurrentDay()} 
              currentMonth={this.getCurrentMonth()}
            />
          </div>
          {/* Router */}
          <div>
            <Settings users={this.state.users} activeUser={this.getActiveUser} addNewUser={this.addNewUser}/>
          </div>
        </div>
      </div>
    )
  }
}



export default App
