import React, { Component } from 'react'
import './App.css';
import Header from './components/layout/Header';
import Search from './components/Search';
import Welcome from './components/Welcome';
import Message from './components/Message';


export class App extends Component {

  state = {
    users: [
      {
        id: 1,
        firstName: "Wajeeh",
        lastName: "Anwar",
        dobDay: 2,
        dobMonth: 3,
        country: "USA",
        active: true
      }, 
      {
        id: 2,
        firstName: "Tabby",
        lastName: "Hussain",
        dobDay: 13,
        dobMonth: 11,
        country: "China",
        active: false
      }
    ]
  }

  getActiveUserName = () => {
    return this.state.users.filter(
      user => {
          return user.active    
      }
  )[0].firstName
  }

  getDate = () => {
    return new Date();
  }

 //activeUser = this.props.users.filter(user => {return user.active})[0]
  render() {
    return (
      <div className="App">
        <div className="container">
          <Header/>
          <Welcome userName={this.getActiveUserName} date={this.getDate}/>
          <Search/>
          <Message users={this.state.users}/>
        </div>
      </div>
    )
  }
}



export default App
