import Quote from './message/Quote';


export class Message extends Component {



isBirthday = () => {
    const today = new Date();
    let currentDay = today.getDate();
    let currentMonth = today.getMonth() + 1;
    if (currentDay === this.props.users.filter(user => {return user.active})[0].dobDay && currentMonth === this.props.users.filter(user => {return user.active})[0].dobMonth) {
        return true
    } else {
        return false
    }
}

isHoliday = () => {
