        [4, 15, "Tax Day"],
        [5, 5, "Cinco de Mayo"],
        [5, 10, "Mother's Day"],
        [5, 25, "Memorial Day"],
        [6, 21, "Father's Day"],
        [7, 3, "Independence Day"],
        [7, 4, "Independence Day"],
        [9, 7, "Labor Day"],
        [10, 12, "Columbus Day"],
        [10, 31, "Halloween"],
        [11, 3, "Election Day"],
        [11, 11, "Veterans Day"],
        [11, 26, "Thanksgiving Day"],
        [11, 27, "Black"],
        [12, 24, "Christmas Eve"],
        [12, 25, "Christmas Day"],
        [12, 31, "New Year's Eve"]
    ];

    const today = new Date();
    let currentDay = today.getDate();
    let currentMonth = today.getMonth() + 1;


    let userCountry = USA;
    switch (this.props.users.filter(user => {return user.active})[0].country) {
        // case "China":
        //     userCountry = China;
        //     break;
        default:
            userCountry = USA;
    }


      let isHoliday = false;
      let holidayName = "";
      for (let i = 0; i < userCountry.length; i++) {
        if (currentDay === userCountry[i][1] && currentMonth === userCountry[i][0]) {
          isHoliday = true;
          holidayName = userCountry[i][2];
        }
      }
      if (isHoliday) {
          return `Happy ${holidayName}!`
      } else {
          return ""
      }
}

message = () => {
    if (this.isBirthday()) {
        return <Birthday/>
    } else if (this.isHoliday() !== "") {
        return <Holiday />
    } else {
        return <Quote />
    }
}


    render() {


        return (
            <div>
                {this.message()}
            </div>
        )
    }
}



export default Message
