import React, { Component } from "react";
import PropTypes from "prop-types";

export class TodoItem extends Component {
  todoTextStyle = () => {
    if (this.props.todo.completed) {
      return "todo-complete";
    } else {
      return "todo-incomplete";
    }
  };

  todoButtonStyle = () => {
    if (this.props.todo.completed) {
      return "todo-complete-button";
    } else {
      return "todo-incomplete-button";
    }
  };

  todoCompleteToggle = () => {
    if (this.props.todo.completed) {
      return "✔";
    } else {
      return "❖";
    }
  };

  render() {
    return (
      <div>
        <div className={`todo-item ${this.todoTextStyle()}`}>
          <div className='todo-text'>{this.props.todo.title}</div>
          <span className='todo-buttons'>
            <input
              type='button'
              className={this.todoButtonStyle()}
              value={this.todoCompleteToggle()}
              onClick={this.props.todoCompleted.bind(this, this.props.todo.id)}
            />
            <button
              type='button'
              className='todo-remove-button'
              onClick={this.props.removeTodo.bind(this, this.props.todo.id)}
            >
              &#10008;
            </button>
          </span>
        </div>
      </div>
    );
  }
}

TodoItem.propTypes = {
  todo: PropTypes.object.isRequired,
  removeTodo: PropTypes.func.isRequired,
  todoCompleted: PropTypes.func.isRequired
};

export default TodoItem;
