import React, { Component } from 'react'

export class Search extends Component {

    onChangeQuery = (e) => this.setState({[e.target.name]: e.target.value});




    // Forward search request to Google.
    onSubmitQuery = (event) => {
     //   const searchForm = document.querySelector("#search-form");
        const searchBar = document.querySelector("#search-bar");
     //   const searchButton = document.querySelector(".search-button");
        searchBar.focus();
    //searchForm.addEventListener('submit', onSubmit());

        event.preventDefault();
        const searchForward = `http://www.google.com/search?q=${searchBar.value}`;
        window.location = searchForward;
    }



    render() {
        return (
            <div>
            <form id="search-form" onSubmit={this.onSubmit}>
              <div id="google-logo"></div>
              <img id="google-logo" src="https://www.google.com/images/branding/googlelogo/2x/googlelogo_color_272x92dp.png" alt="googlelogo"/>
              <input id="search-bar" type="text"/>
              <button 
                className="search-button button" 
                type="submit" 
                value="Google Search" 
                //onClick={this.onSubmitQuery(event)}
                onChange={this.onChangeQuery}
              />
            </form>
            </div>
        )
    }
}

export default Search
