import React, { Component } from 'react'
import CurrentUser from './settings/ActiveUser'
import AdditionalUsers from './settings/AdditionalUsers'
export class Settings extends Component {





    render() {
        return (
            <div>
                <div className="settings-content">
                    <div className="settings-header">
                        <span className="close-settings">&times;</span>
                        <h2>Settings</h2>
                    </div>
                    <div className="settings-body">
                        <CurrentUser activeUser = {this.props.activeUser}/>


                        {console.log(this.props.users.length)}

                        {this.props.users.length > 0 && <AdditionalUsers/>}


                        <div id="settings-buttons">
                            <button type="button" className="settings-button" id="new-user-button">Add User</button>
                            <button type="button" className="settings-button" id="reset-users-button">Reset Users</button>
                        </div>

                        <div id="new-user-window" className="new-user-window">
                            <div className="new-user-content">
                                <div id="new-user-label">
                                    <h3>Create New User</h3>
                                </div>
                            </div>
                        </div>
                    </div>
                    <div className="settings-footer"></div>
                </div>
            </div>
        )
    }
}

